import React from 'react';
import './Homepage.css';
import pic1 from './assets/pic1.jpg'; // Importing the image file

export default function Homepage() {
    return (
        <div className='maincontainer'>
            <div className='para'>
                <h1>
                    React is a powerful JavaScript library for building user interfaces, developed by Facebook. It revolutionized front-end development by introducing a component-based architecture and a virtual DOM, which greatly enhances performance and simplifies the process of building complex UIs. With React, developers can create reusable UI components, making it easier to maintain and scale applications.
                </h1>
            </div>
            <div className='image'>
                {/* Using the imported image file */}
                <img src={pic1} alt='React Homepage Image' />
            </div>
        </div>
    );
}
