import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function DataShowing() {
    const [users, setUsers] = useState([]);
    const [formData, setFormData] = useState({
        Title: '',
        created_By: '',
        createdOn: '',
        content: '',
        DeadLine: ''
    });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = () => {
        axios.get('http://localhost:3000/getdata')
            .then(response => setUsers(response.data))
            .catch(error => console.log(error));
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:3000/postdata', formData)
            .then(response => {
                console.log('Data posted successfully');
                // You may want to update your data after posting
                fetchData();
            })
            .catch(error => console.log(error));
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" name="Title" placeholder="Title" value={formData.Title} onChange={handleInputChange} />
                <input type="text" name="created_By" placeholder="Created By" value={formData.created_By} onChange={handleInputChange} />
                <input type="text" name="createdOn" placeholder="Created On" value={formData.createdOn} onChange={handleInputChange} />
                <input type="text" name="content" placeholder="Content" value={formData.content} onChange={handleInputChange} />
                <input type="text" name="DeadLine" placeholder="DeadLine" value={formData.DeadLine} onChange={handleInputChange} />
                <button type="submit">Submit</button>
            </form>

            <table border={2}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Created By</th>
                        <th>Created On</th>
                        <th>Content</th>
                        <th>DeadLine</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user, index) => (
                        <tr key={index}>
                            <td>{user.Title}</td>
                            <td>{user.created_By}</td>
                            <td>{user.createdOn}</td>
                            <td>{user.content}</td>
                            <td>{user.DeadLine}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}
